CREATE OR ALTER PROC CreateSQLServerlessView_gold
    @ViewName sysname
AS
BEGIN
    DECLARE @statement NVARCHAR(MAX);

    SET @statement = N'
        CREATE OR ALTER VIEW ' + QUOTENAME(@ViewName) + N' AS
        SELECT *
        FROM OPENROWSET(
            BULK ''https://forsynapseaccount.dfs.core.windows.net/gold/Sales/' + @ViewName + N'/'',
            FORMAT = ''DELTA''
        ) AS result;
    ';

    EXEC sys.sp_executesql @statement;
END;
GO
